    private static int n;
    private static long max;
    private static int MAX_LENGTH;
    private static int[] picked;
    private static int[] numbers;

    private static long solution(int[] nums) {
        n = nums.length;
        numbers = nums;
        picked = new int[2];
        MAX_LENGTH = (nums[0] + "").length() * 2;

        comb(0, 0);

        return max;
    }

    private static int[][] getCount() {
        int[][] count = new int[2][10];
        for (int i = 0; i < 2; i++) {
            String str = numbers[picked[i]] + "";
            for (int j = 0; j < str.length(); j++) {
                int cur = str.charAt(j) - '0';
                count[i][cur]++;
            }
        }
        return count;
    }

    private static boolean validate(int[][] count) {
        for (int i = 0; i < 10; i++) {
            if(count[0][i] > 0 && count[1][i] > 0) return false;
        }
        return true;
    }

    private static int findIndex(int[][] count) {
        int start = 1;
        for(int i = 9; i >= 0; i--) {
            if(count[0][i] > 0) return 0;
            else if(count[1][i] > 0) break;
        }
        return start;
    }

    private static void simulation() {
        int[][] count = getCount();
        if(!validate(count)) return;

        int index = findIndex(count);

        StringBuilder sb = new StringBuilder();

        while(sb.length() < MAX_LENGTH) {
            for(int i = 9; i >= 0; i--) {
                if(count[index][i] > 0) {
                    sb.append(i);
                    count[index][i]--;
                    break;
                }
            }
            index = (index + 1) % 2;
        }

        max = Math.max(max, Long.parseLong(sb.toString()));
    }

    private static void comb(int start, int count) {
        if(count == 2) {
            simulation();
            return;
        }

        for(int i = start; i < n; i++) {
            picked[count] = i;
            comb(i + 1, count + 1);
        }
    }
